                    this.ergs = ergs;
                    //Reset memberCount, otherwise the value (state) is saved and re-added in the addition of another ERG.
                    let memberCount = 0;
                    this.ergs.forEach((erg) => {
                        memberCount += erg.numberOfMembers;
                    });
                    this.ergMemberCount = memberCount;
                }
            });
            return;
        }
        catch (err) {
            console.log(err);
            throw err;
        }
    }
