import { BehaviorSubject, firstValueFrom } from 'rxjs';
//Local imports
import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common/http";
import * as i3 from "./alert.service";
export class AccountService {
    constructor(router, http, alertService) {
        this.router = router;
        this.http = http;
        this.alertService = alertService;
        this.userSubject$ = new BehaviorSubject(JSON.parse(localStorage.getItem('user')));
        this.user$ = this.userSubject$.asObservable();
    }
    /**
     * Easy getter for user.
     */
    get userValue() {
        return this.userSubject$.value;
    }
    async authUser(email, password) {
        try {
            console.log('Auth user, meh');
            const response = await firstValueFrom(this.http.post(`${environment.api}/accounts/authentication`, {
                email,
                password,
            }));
            if (response.id) {
                const user = response;
                localStorage.setItem('user', JSON.stringify(user));
                this.userSubject$.next(user);
                return user;
            }
            this.alertService.error(response.error);
            //prompt user to check their email
        }
        catch (err) {
            console.log(err);
            throw err;
        }
    }
    async logout() {
        localStorage.removeItem('user');
        this.userSubject$.next(null);
        this.router.navigate(['/login']);
    }
}
