import { __decorate } from "tslib";
//Ng imports
import { Injectable } from '@angular/core';
import { BehaviorSubject, firstValueFrom } from 'rxjs';
//Local imports
import { environment } from 'src/environments/environment';
let AccountService = class AccountService {
    constructor(router, http, alertService) {
        this.router = router;
        this.http = http;
        this.alertService = alertService;
        this.userSubject$ = new BehaviorSubject(JSON.parse(localStorage.getItem('user')));
        this.user$ = this.userSubject$.asObservable();
    }
    /**
     * Easy getter for user.
     */
    get userValue() {
        return this.userSubject$.value;
    }
    async authUser(email, password) {
        try {
            console.log('Auth user, meh');
            const response = await firstValueFrom(this.http.post(`${environment.api}/accounts/authentication`, {
                email,
                password,
            }));
            if (response.id) {
                const user = response;
                localStorage.setItem('user', JSON.stringify(user));
                this.userSubject$.next(user);
                return user;
            }
            //prompt user to check their email
        }
        catch (err) {
            console.log(err);
            throw err;
        }
    }
    async logout() {
        localStorage.removeItem('user');
        this.userSubject$.next(null);
        this.router.navigate(['/login']);
    }
};
AccountService = __decorate([
    Injectable({
        providedIn: 'root',
    })
], AccountService);
export { AccountService };
